mapper.insert:

INSERT INTO board
SET writer_id = #{writerId}
    , title = #{title}
    , contents = #{contents}

mapper.select:

SELECT board_id AS boardId, writer_id AS writerId, title, contents, created_at AS createdAt, updated_at AS updatedAt
FROM board
WHERE board_id = #{boardId}

mapper.update:

UPDATE board


        , title = #{title}


        , contents = #{contents}


WHERE board_id = #{boardId}
